INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('deepseek', 'Deepseek', 'DeepSeek is a large language model developed by Beijing DeepSeek AI. It is designed for intelligent conversation scenarios and serves enterprise customers. Based on user input text data, it generates text, code, and other content that meets user requirements through the large language model.', '/static/images/model/deepseek.svg', 'llm');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('default', 'KAI', 'KAI is an innovative knowledge base application powered by large language models, providing you with a comprehensive and intelligent solution that makes knowledge management simpler and more efficient.', '/static/images/model/kai.png', 'text-embedding');
INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('moonshot', 'Moonshot', 'Moonshot text generation models (moonshot-v1) are trained to understand natural and written language. They can generate text output based on input. The input to the model is called a prompt. moonshot-v1 models can be used for various tasks including content/code generation, summarization, dialogue, and creative writing.', '/static/images/model/moonshot.png', 'llm');
