INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('ollama', 'Ollama', 'Ollama is a powerful framework designed to deploy LLMs locally. It simplifies the process of deploying and managing LLMs, helping users quickly run large models locally. With simple installation commands, users can run open-source large language models with just one command.', '/static/images/model/ollama.svg', 'llm,text-embedding');
