INSERT INTO "t_sys_model_prvd_info" ("prvd_id", "prvd_nm", "prvd_desc", "prvd_icon", "modl_typ") VALUES ('tongyi', 'Tongyi Qianwen', 'Tongyi Qianwen is an AI assistant with comprehensive capabilities, designed to help with work, study, and daily life. Features include multi-turn dialogue, content creation, logical reasoning, multimodal understanding, and multilingual support.', '/static/images/model/tongyi.png', 'llm,text-embedding,tts');
